sources.sass\\utils\\_variables.scss:
:root {
  --accent-primary: #d41443;
  --accent-secondary: #ffa5ba;
  --accent-yellow: #f0d1a5;
  --accent-green: #c2e297;
  --button-contact: #d41342;
  --contact-border: #e1e1e1;

  --white-color: #ffffff;
  --text-dark: #000000;
  --text-light: #907e82;

  --hover-button-dark: #c7133e;
  --hover-button-light: #ecebeb;
  --slider: #d31342;
  --clients: #d9d9d9;

  --bg-menu: #e17992;
  --bg-secondary: #fff5f6;
  --bg-clients: #fafafa;
  --bg-hero-prod: #ffb8ca;

  --button-contact: #d41342;
  --contact-border: #e1e1e1;

  --transition-time: 250ms;
  --cubic-bezier: cubic-bezier(0.4, 0, 0.2, 1);
  --animation: var(--transition-time) cubic-bezier(0.4, 0, 0.2, 1);
}

@mixin font($fs, $lh, $ls, $fw: 500) {
  font-size: $fs;
  line-height: $lh;
  letter-spacing: $ls;
  font-weight: $fw;
}

sources.sass\\utils\\_base.scss:
body {
  margin: 0;
  font-family: 'DM Sans', sans-serif;
  letter-spacing: 0.04em;

  color: var(--text-dark);
}

h1,
h2,
h3,
h4,
h5,
h6,
p,
ul {
  margin: 0;
  padding: 0;
}

img {
  display: block;
  max-width: 100%;
  height: auto;
}

/* -------All site------- */

*,
::after,
::before {
  box-sizing: border-box;
}

.subtitle {
  font-family: 'Titan One';
  font-weight: 400;
  font-size: 18px;
  line-height: 1.16;
  text-align: center;
  text-transform: uppercase;

  @media screen and (min-width: 768px) {
    font-size: 26px;
    line-height: 1.15;
  }

  @media screen and (min-width: 1280px) {
    font-size: 30px;
    line-height: 1.13;
  }
}

.title {
  font-family: 'Titan One';
  font-weight: 400;
  font-size: 34px;
  line-height: 1.14;
  text-align: center;
  letter-spacing: 0.04em;
  text-transform: uppercase;

  @media screen and (min-width: 768px) {
    font-size: 48px;
    line-height: 1.14;
  }

  @media screen and (min-width: 1280px) {
    font-size: 58px;
    line-height: 1.13;
    letter-spacing: 0.06em;
  }
}

.link {
  text-decoration: none;
}

.list {
  list-style: none;
}

.container {
  padding-left: 20px;
  padding-right: 20px;
  margin-right: auto;
  margin-left: auto;
  min-width: 320px;

  @media screen and (min-width: 320px) {
    width: 100%;
  }

  @media screen and (min-width: 480px) {
    width: 480px;
  }

  @media screen and (min-width: 768px) {
    padding-left: 36px;
    padding-right: 36px;
    width: 768px;
    &--adv {
      padding-right: 76px;
    }
  }

  @media screen and (min-width: 1280px) {
    width: 1280px;
    &--adv {
      padding-left: 115px;
      padding-right: 115px;
    }
  }
}


body.lock {
  overflow: hidden;
}

sources.sass\\utils\\_visually-hidden.scss:
.visually-hidden {
	position: absolute;
	width: 1px;
	height: 1px;
	margin: -1px;
	border: 0;
	padding: 0;
	white-space: nowrap;
	clip-path: inset(100%);
	clip: rect(0 0 0 0);
	overflow: hidden;
}

sources.sass\\utils\\_arrow.scss:
.arrow {
  position: relative;
}
.scrollup {
  text-align: center;
  padding: 7px;
  position: fixed;
  right: 15px;
  bottom: 15px;
  color: rgba(212, 20, 68, 0.7);
  background-image: linear-gradient(90deg, rgba(255, 165, 186, 0.7), rgba(255, 165, 186, 0.7));
  width: 44px;
  height: 44px;
  border-radius: 50%;
  cursor: pointer;
  display: none;
  z-index: 10;
  box-shadow: none;
  box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.12), 0px 1px 1px rgba(0, 0, 0, 0.14),
    0px 2px 1px rgba(0, 0, 0, 0.2);
  &:hover {
    background-image: linear-gradient(90deg, rgba(194, 226, 151, 0.7), rgba(194, 226, 151, 0.7));
  }
  &-show {
    display: block;
  }
}
.scrollup__icon {
  width: 30px;
  height: 30px;
  fill: currentColor;
}

.scrollup-show {
  display: block;
}

