.mob-menu {
  position: fixed;
  z-index: 40;
  top: 0;
  right: 0;
  width: 250px;
  height: 100vh;
  overflow-y: scroll;
  transform: translateX(100%);
  background-color: var(--bg-menu);
  box-shadow: (-8px 0px 20px rgba(0, 0, 0, 0.25));

  transition: transform var(--transition-time) var(--cubic-bezier);
  @media screen and (min-width: 768px) {
    width: 254px;
  }
  @media screen and (min-width: 1280px) {
    display: none;
  }
}

.js-menu-container.is-open .mob-menu {
  transform: translateX(0);
  // transition: transform var(--transition-time) var(--cubic-bezier);
}

.close-menu-button {
  margin: 23px 22px 24px auto;
  width: 30px;
  height: 30px;
  border: none;
  background-color: transparent;
  display: flex;
  fill: var(--white-color);
  transition: fill var(--transition-time) var(--cubic-bezier);
  &:hover,
  &:focus {
    fill: #d41443;
    cursor: pointer;
  }
  @media screen and (min-width: 768px) {
    margin-right: 24px;
  }
}
.burger-close {
  width: 30px;
  height: 30px;
}
.burger__button {
  padding-left: 21px;
  padding-right: 21px;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 210px;
  height: 45px;
  margin: 0 auto 20px auto;
  transition: var(--transition-time) var(--cubic-bezier);
  &:hover,
  &:focus {
    cursor: pointer;
    background-color: #ecebeb;
  }
  @include font(16px, 1.31, 0.04em, 700);
  border-radius: 22px;
  color: var(--accent-primary);
  background-color: var(--white-color);
  border: none;
}

.burger__item {
  padding-left: 21px;
  padding-right: 21px;
  border-top: 1px solid #e18298;
  padding-bottom: 11px;
  padding-top: 13px;
  &:last-child {
    border-bottom: 1px solid #e18298;
  }
  @media screen and (min-width: 768px) {
    padding-left: 24px;
  }
}
.burger__link {
  color: var(--white-color);
  @include font(16px, 1.31, 0.03em, 700);
  transition: var(--transition-time) var(--cubic-bezier);
  &:hover,
  &:focus {
    color: var(--accent-primary);
  }
}
.burger__link.current {
  color: var(--accent-text-color);
}

sources.sass\\_header.scss:
.header__container {
  display: flex;
  position: absolute;
  top: 0;
  left: 50%;
  transform: translate(-50%) scale(1);
  align-items: center;
  padding: 0 20px;
  z-index: 3;
  background-color: transparent;
  color: #d41443;
  @media screen and (min-width: 320px) {
    width: 100%;
  }
  @media screen and (min-width: 480px) {
    width: 480px;
  }
  @media screen and (min-width: 768px) {
    width: 768px;
    padding: 0 35px;
  }
  @media screen and (min-width: 1280px) {
    width: 1280px;
    padding-left: 45px;
    padding-right: 94px;
  }
}

.header__logo {
  display: inline-block;
  @media screen and (min-width: 1280px) {
    margin-right: 110px;
  }
}
.logo__header {
  width: 200px;
  padding: 6px 0;
  @media screen and (min-width: 768px) {
    padding: 37px 0;
  }
}

.open-menu-button {
  margin-left: auto;
  border: none;
  background-color: transparent;
  fill: currentColor;
  @media screen and (min-width: 1280px) {
    display: none;
  }
  & :hover,
  & :focus {
    cursor: pointer;
  }
}
.header__list {
  display: flex;
  @media screen and (max-width: 1279px) {
    display: none;
  }
  @media screen and (min-width: 1280px) {
    margin-right: 110px;
  }
}
.header__item {
  & + & {
    margin-left: 35px;
  }
}
.header__link {
  color: var(--white-color);
  padding: 50px 0;
  @include font(16px, 1.31, 0.03em, 700);
  display: flex;
  align-items: center;
  transition: var(--transition-time) var(--cubic-bezier);
  &:hover,
  &:focus {
    color: var(--accent-primary);
  }
}

.header__button {
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  width: 160px;
  height: 44px;
  transition: var(--transition-time) var(--cubic-bezier);
  &:hover,
  &:focus {
    background-color: #ecebeb;
  }
  @include font(16px, 1.31, 0.04em, 700);
  border-radius: 22px;
  color: var(--accent-primary);
  background-color: var(--white-color);
  border: none;
  @media screen and (max-width: 768px) {
    display: none;
  }
  @media screen and (min-width: 768px) and (max-width: 1279px) {
    margin-left: 30px;
  }
}

sources.sass\\_hero.scss:
.hero-section {
  position: relative;

  margin-left: auto;
  margin-right: auto;

  height: 502px;
  background-color: var(--bg-hero-prod);
  overflow: hidden;

  @media screen and (min-width: 768px) {
    height: 399px;
  }

  @media screen and (min-width: 1280px) {
    height: 665px;
  }
}

.hero-container {
  position: relative;

  width: 320px;
  height: 100%;

  @media screen and (min-width: 768px) {
    width: 768px;
  }

  @media screen and (min-width: 1280px) {
    width: 1280px;
  }
}

.hero-mob {
  @media screen and (max-width: 767px) {
    position: relative;

    width: 320px;
    height: 100%;
    margin-left: auto;
    margin-right: auto;
  }
}

.hero-circle {
  position: absolute;
  bottom: 75px;
  left: 36px;

  width: 346px;
  height: 344px;
  border-radius: 50%;
  background-color: var(--accent-secondary);

  animation: fadeInAnimation ease 3s;

  @keyframes fadeInAnimation {
    0% {
      transform: translateY(50%);
    }
    100% {
      transform: translateY(0);
    }
  }

  @media screen and (min-width: 768px) {
    bottom: 46px;
    left: 307px;

    width: 324px;
    height: 322px;
  }

  @media screen and (min-width: 1280px) {
    bottom: 75px;
    left: 475px;

    width: 538px;
    height: 538px;
  }
}

.hero-ice-cream-img {
  position: absolute;
  bottom: 0;
  left: 117px;

  animation: fadeInAnimation ease 2s;

  @keyframes fadeInAnimation {
    0% {
      transform: translateY(40%);
      opacity: 0.2;
    }
    100% {
      transform: translateY(0);
      opacity: 1;
    }
  }

  @media screen and (min-width: 768px) {
    left: 378px;
  }

  @media screen and (min-width: 1280px) {
    left: 594px;
  }
}

.hero-wrap {
  position: absolute;
  left: 0;
  bottom: 160px;

  @media screen and (min-width: 768px) {
    left: 34px;
    bottom: 173px;
  }

  @media screen and (min-width: 1280px) {
    left: 115px;
    bottom: 292px;
  }
}

.hero-wrap__title {
  font-family: 'Titan One';
  @include font(26px, 1.15, 0.04em, 400);
  color: var(--white-color);
  text-transform: uppercase;

  margin-bottom: 19px;

  @media screen and (min-width: 768px) {
    @include font(22px, 1.13, 0.04em, 400);
  }

  @media screen and (min-width: 1280px) {
    margin-bottom: 25px;
    @include font(38px, 1.15, 0.04em, 400);
  }
}

.hero-wrap__title-accent {
  display: block;
  font-family: 'Titan One';
  @include font(48px, 0.95, 0.04em, 400);
  color: var(--accent-primary);
  text-transform: uppercase;

  @media screen and (min-width: 768px) {
    @include font(34px, 0.91, 0.04em, 400);
  }

  @media screen and (min-width: 1280px) {
    margin-top: -7px;

    @include font(58px, 0.9, 0.04em, 400);
  }
}

.hero-links {
  display: flex;
}

.hero-links__product-link,
.hero-links__how-link {
  @include font(8px, 1.25, 0, 400);

  display: flex;
  align-items: center;
  justify-content: center;

  height: 24px;
  border-radius: 20px;

  @media screen and (min-width: 1280px) {
    height: 40px;
    @include font(14px, 1.28, 0, 400);
  }
}

.hero-links__product-link {
  width: 62px;

  color: var(--white-color);
  background-color: var(--accent-primary);
  transition: background-color var(--animation);

  &:hover,
  &:focus {
    color: var(--white-color);
    background-color: var(--hover-button-dark);
  }

  @media screen and (min-width: 1280px) {
    width: 104px;
  }
}

.hero-links__how-link {
  margin-left: 8px;
  width: 83px;

  color: var(--accent-primary);
  background-color: var(--white-color);
  transition: background-color var(--animation);

  &:hover,
  &:focus {
    color: var(--accent-primary);
    background-color: var(--hover-button-light);
  }

  @media screen and (min-width: 1280px) {
    width: 138px;
    margin-left: 15px;
  }
}

.hero-features {
  display: none;
  @media screen and (min-width: 768px) {
    display: block;
  }
}

.hero-features-numbers {
  position: absolute;
  right: 44px;
  top: 96px;

  @media screen and (min-width: 1280px) {
    right: 115px;
    top: 160px;
  }
}

.hero-features-numbers__top::before,
.hero-features-numbers__bottom::before {
  content: '';
  position: absolute;
  right: -10px;
  top: -2px;

  display: block;
  width: 16px;
  height: 16px;
  border-radius: 50%;

  background-color: var(--white-color);

  @media screen and (min-width: 1280px) {
    width: 30px;
    height: 30px;
    right: -18px;
    top: -4px;
  }
}

.hero-features-numbers__bottom::before {
  right: -8px;

  @media screen and (min-width: 1280px) {
    right: -18px;
  }
}

.hero-features-numbers__top {
  margin-bottom: 14px;

  @media screen and (min-width: 1280px) {
    margin-bottom: 18px;
  }
}

.hero-features-numbers__bottom {
  position: relative;
  margin-right: -2px;
  @media screen and (min-width: 1280px) {
    margin-right: 0;
  }
}

.hero-features-numbers__number {
  position: relative;

  text-align: right;

  font-family: 'Titan One';
  @include font(28px, 1.14, 0.04em, 400);

  text-transform: uppercase;

  color: var(--accent-primary);

  @media screen and (min-width: 1280px) {
    @include font(46px, 1.15, 0.04em, 400);
  }
}

.hero-features-numbers__text {
  text-align: right;

  @include font(8px, 1.25, 0, 400);

  color: var(--white-color);

  @media screen and (min-width: 1280px) {
    @include font(14px, 1.28, 0, 400);
  }
}

.hero-features-info {
  position: absolute;
  bottom: 28px;
  left: 34px;

  display: flex;

  animation: fadeInAnimationInfo ease 1.8s;

  @keyframes fadeInAnimationInfo {
    0% {
      transform: translateX(-50px);
    }
    100% {
      transform: translateX(0);
    }
  }

  @media screen and (min-width: 1280px) {
    left: 77px;
    bottom: 45px;
  }
}

.hero-features-info__wrap {
  margin-top: auto;
  margin-bottom: 6px;
  margin-left: -4px;

  @media screen and (min-width: 1280px) {
    margin-left: 30px;
    margin-bottom: 11px;
  }
}

.hero-features-info__text {
  width: 114px;
  margin-bottom: 7px;

  @include font(8px, 1.5, 0, 400);

  color: var(--white-color);

  @media screen and (min-width: 1280px) {
    margin-bottom: 20px;
    width: 200px;
    @include font(14px, 1.42, 0, 400);
  }
}

.hero-features-info__pic {
  position: absolute;
  bottom: 0;
  right: 34px;

  animation: fadeInAnimationpic ease 1.5s;

  @keyframes fadeInAnimationpic {
    0% {
      transform: translateX(50px);
    }
    100% {
      transform: translateX(0);
    }
  }

  @media screen and (min-width: 1280px) {
    right: 109px;
  }
}

sources.sass\\_prod.scss:
.prod {
  padding-top: 121px;
  padding-bottom: 98px;
  @media screen and (min-width: 768px) {
    padding-bottom: 112px;
  }
  @media screen and (min-width: 1280px) {
    padding-top: 127px;
    padding-bottom: 119px;
  }
  .container {
    @media screen and (max-width: 767px) {
      max-width: 320px;
    }
    @media screen and (min-width: 768px) {
      padding: 0px 34px;
    }
    @media screen and (min-width: 1280px) {
      padding: 0px 116px;
    }
  }
}
.prod__subtitle {
  margin-bottom: 15px;
  color: var(--accent-secondary);
}
.prod__title {
  margin-bottom: 144px;
  color: var(--accent-primary);
  @media screen and (min-width: 768px) {
    margin-bottom: 150px;
  }
  @media screen and (min-width: 1280px) {
    margin-bottom: 259px;
  }
}
.prod-list {
  margin-bottom: -130px;
  @media screen and (min-width: 768px) {
    display: flex;
    flex-wrap: nowrap;
    margin-left: -20px;
  }
  @media screen and (min-width: 768px) {
    margin-left: -30px;
  }
}
.prod-list__item {
  padding: 161px 39px 41px 41px;
  text-align: center;
  margin-bottom: 130px;
  @media screen and (min-width: 768px) {
    max-width: 220px;
    margin-left: 20px;
    flex-basis: calc(100% / 3 - 20px);
    padding: 152px 11px 40px;
  }
  @media screen and (min-width: 1280px) {
    min-width: 329px;
    margin-left: 30px;
    flex-basis: calc(100% / 3 - 30px);
    padding: 163px 44px 50px;
  }
}
.prod-card {
  position: relative;
  border-radius: 24px;
  font-size: 0px;
  &:nth-child(1) {
    background-color: var(--bg-hero-prod);
    .prod-card__title::before {
      background-image: url(/images/prod/prod-mob-icecream-1x.png);

      @media (min-device-pixel-ratio: 2),
        (-webkit-min-device-pixel-ratio: 2),
        (min-resolution: 192dpi),
        (min-resolution: 2dppx) {
        background-image: url(/images/prod/prod-mob-icecream-1x.png);
      }
      @media screen and (min-width: 768px) {
        background-image: url(/images/prod/prod-tab-icecream-1x.png);

        @media (min-device-pixel-ratio: 2),
          (-webkit-min-device-pixel-ratio: 2),
          (min-resolution: 192dpi),
          (min-resolution: 2dppx) {
          background-image: url(/images/prod/prod-tab-icecream-2x.png);
        }
      }
      @media screen and (min-width: 1280px) {
        background-image: url(/images/prod/prod-desk-icecream-1x.png);

        @media (min-device-pixel-ratio: 2),
          (-webkit-min-device-pixel-ratio: 2),
          (min-resolution: 192dpi),
          (min-resolution: 2dppx) {
          background-image: url(/images/prod/prod-desk-icecream-2x.png);
        }
      }
    }
  }
  &:nth-child(2) {
    background-color: var(--accent-yellow);
    .prod-card__title::before {
      background-image: url(/images/prod/prod-mob-icecoffee-1x.png);
      @media (min-device-pixel-ratio: 2),
        (-webkit-min-device-pixel-ratio: 2),
        (min-resolution: 192dpi),
        (min-resolution: 2dppx) {
        background-image: url(/images/prod/prod-mob-icecoffee-2x.png);
      }
      @media screen and (min-width: 768px) {
        background-image: url(/images/prod/prod-tab-icecoffee-1x.png);
        @media (min-device-pixel-ratio: 2),
          (-webkit-min-device-pixel-ratio: 2),
          (min-resolution: 192dpi),
          (min-resolution: 2dppx) {
          background-image: url(/images/prod/prod-tab-icecoffee-2x.png);
        }
      }
      @media screen and (min-width: 1280px) {
        background-image: url(/images/prod/prod-desk-icecoffee-1x.png);
        @media (min-device-pixel-ratio: 2),
          (-webkit-min-device-pixel-ratio: 2),
          (min-resolution: 192dpi),
          (min-resolution: 2dppx) {
          background-image: url(/images/prod/prod-desk-icecoffee-2x.png);
        }
      }
    }
  }
  &:nth-child(3) {
    background-color: var(--accent-green);
    .prod-card__title::before {
      background-image: url(/images/prod/prod-mob-milkshakes-1x.png);
      @media (min-device-pixel-ratio: 2),
        (-webkit-min-device-pixel-ratio: 2),
        (min-resolution: 192dpi),
        (min-resolution: 2dppx) {
        background-image: url(/images/prod/prod-mob-milkshakes-2x.png);
      }
      @media screen and (min-width: 768px) {
        background-image: url(/images/prod/prod-tab-milkshakes-1x.png);
        @media (min-device-pixel-ratio: 2),
          (-webkit-min-device-pixel-ratio: 2),
          (min-resolution: 192dpi),
          (min-resolution: 2dppx) {
          background-image: url(/images/prod/prod-tab-milkshakes-2x.png);
        }
      }
      @media screen and (min-width: 1280px) {
        background-image: url(/images/prod/prod-desk-milkshakes-1x.png);
        @media (min-device-pixel-ratio: 2),
          (-webkit-min-device-pixel-ratio: 2),
          (min-resolution: 192dpi),
          (min-resolution: 2dppx) {
          background-image: url(/images/prod/prod-desk-milkshakes-2x.png);
        }
      }
    }
  }
}
.prod-card__title {
  margin-bottom: 66px;
  font-family: 'Titan One';
  @include font(20px, 1.15, 0.04em, 400);
  text-align: center;
  text-transform: uppercase;
  color: var(--white-color);
  @media screen and (min-width: 1280px) {
    margin-bottom: 70px;
    @include font(30px, 1.13, 0.04em, 400);
  }
  &::before {
    position: absolute;
    content: '';
    top: -113px;
    left: 50%;
    transform: translate(-50%);
    width: 100%;
    height: 252px;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    @media screen and (min-width: 768px) {
      height: 226px;
      top: -104px;
    }
    @media screen and (min-width: 1280px) {
      height: 335px;
      top: -210px;
    }
  }
  &::after {
    content: '';
    position: absolute;
    top: 214px;
    left: 50%;
    transform: translate(-50%);
    width: 26px;
    height: 6px;
    background-image: url(/images/prod/prod-dots.svg);
    @media screen and (min-width: 768px) {
      top: 205px;
    }
    @media screen and (min-width: 1280px) {
      top: 229px;
    }
  }
}
.prod-card__text {
  margin-bottom: 28px;
  @include font(14px, 1.5, 0.04em, 700);
  text-align: center;
  color: var(--white-color);
  @media screen and (min-width: 768px) {
    margin-bottom: 40px;
    @include font(14px, 1.78, 0.04em, 700);
  }
  @media screen and (min-width: 1280px) {
    margin-bottom: 56px;
    @include font(16px, 1.75, 0.04em, 700);
  }
}
.prod-card__text--is-hidden {
  @media screen and (min-width: 768px) {
    display: none;
  }
}
.prod-card__button {
  cursor: pointer;
  border: 0;
  padding: 0;
  background-color: transparent;
  border-radius: 50%;
  transform: rotate(0);
  transition: var(--animation);
}

.prod-card__button:hover,
.prod-card__button:focus {
  transform: rotate(90deg);
  // box-shadow: -1px 0px 9px 0px rgba(0, 0, 0, 0.1);
  & + .prod-card__copy {
    opacity: 1;
    pointer-events: auto;
    transform: translateY(100%);
  }
}
.prod-card__wrap {
  position: relative;
}
.prod-card__copy {
  position: absolute;
  z-index: 2;
  width: 280px;
  bottom: -5px;
  left: -41px;
  @include font(14px, 1.5, 0.04em, 700);
  border-radius: 0px 0px 24px 24px;
  padding: 20px 44px 20px 44px;
  transition: var(--animation);
  opacity: 0;
  pointer-events: none;
  transform: translateY(85%);
  color: var(--white-color);
  &--pink {
    background: var(--bg-hero-prod);
  }
  &--yellow {
    background: var(--accent-yellow);
  }
  &--green {
    background: var(--accent-green);
  }

  @media screen and (min-width: 768px) {
    @include font(14px, 1.4, 0.04em, 500);
    width: 220px;
    left: -11px;
    padding: 20px 11px 20px 11px;
  }
  @media screen and (min-width: 1280px) {
    @include font(16px, 1.75, 0.04em, 500);
    width: 329px;
    left: -44px;
    padding: 20px 44px 20px 44px;
  }
}

.prod-card__add-text {
  margin-bottom: 5px;
}

sources.sass\\_about.scss:
.about {
  padding-top: 125px;
  padding-bottom: 39px;
  background-color: var(--bg-secondary);

  background-image: url('../images/about/about-mob-bg-1x.png');
  background-repeat: no-repeat;
  background-position-y:88%;
  background-size:contain;

  @media (min-device-pixel-ratio: 2),(min-resolution: 192dpi),(min-resolution: 2dppx) {
    background-image: url('../images/about/about-mob-bg-2x.png');
  }

  @media screen and (min-width: 768px) {
    padding-top: 130px;
    padding-bottom: 66px;
    background-image: none;
  }

  @media screen and (min-width: 1280px) {
    background-image: url('../images/about/about-desk-bg-1x.png');
    @media (min-device-pixel-ratio: 2),(min-resolution: 192dpi),(min-resolution: 2dppx) {
      background-image: url('../images/about/about-desk-bg-2x.png');
    }
    background-position-y:100%;
  }
}

.about__container{
  @media screen and (min-width: 768px){
  padding-left: 39px;
  padding-right: 36px;
  }
  @media screen and (min-width: 1280px) {
  padding-left: 112px;
  padding-right: 117px;
  }
}
.about__subtitle {
  color: var(--accent-secondary);
  margin-bottom: 15px;
}
.about__title {
  color: var(--accent-primary);
  margin-bottom: 26px;

  @media screen and (min-width: 768px) {
  margin-bottom: 50px;
  }
  @media screen and (min-width: 1280px) {
  margin-bottom: 55px;
  }
}
.about__information {
  width: 100%;
  @media screen and (min-width: 768px){
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  }
  // @media screen and (min-width: 1280px){
  //   justify-content:space-evenly
  // }
}
.about__image {
  width: 100%;
  margin-left: auto;
  margin-right: auto;
  
  @media screen and (max-width: 767.98px) {
    margin-bottom: 46px;
  }
}
.about__main-text {
  @include font (12px,1.62,0em,500);
  color: var(--text-dark);
  margin-bottom: 30px;

  @media screen and (min-width: 768px) {
    @include font (14px,1.76,0,500);
  }
  @media screen and (min-width: 1280px) {
    @include font (16px,1.76,0.04em,500);
  }
}
.about__description-text {
  @include font (12px,1.62,0,500);
  color: var(--text-light);

  &--first {
    margin-bottom: 18px;
  }
  &--second {
    margin-bottom: 25px;
  }

  @media screen and (min-width: 768px) {
    @include font (14px,1.76,0,500);

    &--second {
    margin-bottom: 62px;
  }
  }

  @media screen and (min-width: 1280px) {
    @include font (14px,1.94,0,500);

  &--first {
    margin-bottom: 35px;
  }
  &--second {
    margin-bottom: 45px;
  }
  }
}
.about__button {
  cursor: pointer;
  font-family: inherit;
  @include font (14px,1.3,0.04em,700);
  color:var(--accent-primary);
  background-color: var(--white-color);
  border: none;
  border-radius: 22px;
  min-width: 176px;
  height: 44px;

  display: flex;
  justify-content: center;
  align-items: center;

  padding-left: 37px;
  padding-right: 37px;
  margin: auto 0;   
  transition: color var(--animation), background-color var(--animation);

  &:focus,
  &:hover {
  background-color:var(--hover-button-light);
  }
  &::after {
    content: '';
    margin-left: 13px;
    height: 10px;
    width: 10px;

    background-image: url('../images/about/about-icon.png');
    background-repeat: no-repeat;
    background-position: 50% 50%;
  }
}
.about__description {
  padding-left: 30px;
  padding-right: 30px;
  overflow: hidden;

  @media screen and (min-width: 768px) {
    width: 340px;
    padding-top: 55px;
    padding-left: 0;
    padding-right: 0;
  }
  @media screen and (min-width: 1280px){
    width: 463px;
    padding-top: 47px;
    padding-left: 0;
    padding-right: 0;
  }
}
.backdrop {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 3;
  width: 100vw;
  height: 100vh;
  background-color:rgba(255,221,225,0.2);
  opacity: 1;
  transition: opacity var(--animation), visibility var(--animation);
  
  &.is-hidden {
  visibility: hidden;
  opacity: 0;
  pointer-events: none;
  }
  &.is-hidden.modal {
  opacity: 0;
  transform: scale(0.5) translate(-50%, -50%);
  } 
}
.modal-about {
  position: absolute;
  top: 50%;
  left: 50%;
  width: 98%;
  height: 98%;
  padding: 30px;

  color:var(--accent-primary);
  background-color:var(--bg-secondary);
  background-image: linear-gradient(90deg, rgba(255,221,225,0.3) 35%, rgba(238,156,167,0.3) 100%);
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;

  border-radius: 24px;
  box-shadow: 0 8px 30px var(--contact-border);
  opacity: 1;
  transform: scale(1) translate(-50%, -50%);
  transition: opacity var(--animation), opacity var(--animation);

  @media screen and (max-width: 1279.98px) {
    max-width: 460px;
    height: auto;
  }
  @media screen and (min-width: 1280px) {
    width: 520px;
    height: 560px;
  }
}
.modal-about__title {
  @include font (40px,1.2,0.04em,500);
  color:var(--accent-primary);
  margin-bottom: 25px;
}
.modal-about__subtitle {
  @include font (20px,1,0.04em,500);
  color:var(--accent-green);
  margin-bottom: 15px;
}
.modal-about__text {
  @include font (12px,1.62,0.04,500);
  color: var(--text-light);
  margin-bottom: 15px;
  text-align: center;
}
.modal-about__close-button {
  position: absolute;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;

  top: 10px;
  right: 10px;
  width: 25px;
  height: 25px;
  color:var(--accent-primary);
  background-color: transparent;
  border: 1px solid var(--accent-primary);
  border-radius: 50%;
    
  &:focus,
  &:hover {
    color: var(--accent-green);
    background-color: var(--accent-primary);
  }
}
.modal-about__icon-close {
  fill: currentColor;
}

